import javax.crypto.spec.GCMParameterSpec
import javax.crypto.SecretKey
import java.util.Base64
import javax.crypto.Cipher
import javax.crypto.spec.SecretKeySpec
import java.security.PrivateKey
import com.vordel.security.cert.PersonalInfo
import com.vordel.store.cert.CertStore

def invoke(msg) {
        int TAG_BIT_LENGTH = 128
        String ALGO_TRANSFORMATION_STRING = "AES/GCM/PKCS5Padding"

        // Extracting key, data, iv and cert alias name from input payload
        String enckey = msg.get("enckey")
        String encText = msg.get("encText")
        String certificateAlias = msg.get("certAlias")
        String iv = msg.get("iv_vector")

        // Extract private cert from certstore
        CertStore certs = CertStore.getInstance()
        PersonalInfo pi = certs.getPersonalInfoByAlias(certificateAlias)
        PrivateKey privKey = pi.privateKey

        // Exract symmetric key for data decryption
        Cipher rsaCipher = Cipher.getInstance("RSA/ECB/PKCS1Padding")
        rsaCipher.init(Cipher.DECRYPT_MODE, privKey)
        byte[] aesKeyBytes = rsaCipher.doFinal(hexToByteArray(enckey))

        // Base64 decode the AES key
        byte[] decodedKeyBytes = Base64.getDecoder().decode(aesKeyBytes)
        SecretKeySpec aesKey = new SecretKeySpec(decodedKeyBytes, "AES")
     
	    // Convert the AES key to a Base64-encoded string if you need to store or display it
        String symmetricKey = Base64.getEncoder().encodeToString(decodedKeyBytes)
        msg.put("symmetricKey", symmetricKey)
	  byte[] iv_vector = Base64.getDecoder().decode(iv);
		
		
        // Decrypt the data with the AES key
        GCMParameterSpec gcmParamSpec = new GCMParameterSpec(TAG_BIT_LENGTH, iv_vector)
        Cipher aesCipher = Cipher.getInstance(ALGO_TRANSFORMATION_STRING)
        aesCipher.init(Cipher.DECRYPT_MODE, aesKey, gcmParamSpec)
        byte[] decryptedDataBytes = aesCipher.doFinal(hexToByteArray(encText))
        String decryptedData = new String(decryptedDataBytes)

        msg.put("decryptedData", decryptedData);

    return true
}
    public static byte[] hexToByteArray(String hexString) {
        // Validate the hex string length
        if (hexString.length() % 2 != 0) {
            throw new IllegalArgumentException("Hex string must have an even length");
        }

        // Prepare byte array
        byte[] byteArray = new byte[hexString.length() / 2];

        // Convert each pair of hex characters to a byte
        for (int i = 0; i < hexString.length(); i += 2) {
            String hexPair = hexString.substring(i, i + 2); // Extract 2 characters
            byteArray[i / 2] = (byte) Integer.parseInt(hexPair, 16); // Convert to byte
        }

        return byteArray;
    }

